INSERT INTO tblAgency(agencySeq,agency,agencyURL,agencyTel,agencyInfo) values (agencySeq.nextval, '고용노동부', 'http://www.moel.go.kr/', '02-1544-1032', '고용노동부은 국회나 그 위원회의 요구가 있을 때에는 국무총리·국무위원 또는 정부위원은 출석·답변하여야 하며, 국무총리 또는 국무위원이 출석요구를 받은 때에는 국무위원 또는 정부위원으로 하여금 출석·답변하게 할 수 있다.');
