INSERT INTO tblCrt(crtSeq,crtName,crtTypeSeq,expiration,agencySeq,isRoutine,difficulty,likeCnt,qualificationCate,qualification,crtInfo,totalCnt,jobDesc) values ('7524','인쇄기능사','1','없음','96','1','2','63','없음','다음 결격사유에 해당하지 않은 자 
