mapper.select:
SELECT * FROM (SELECT b.*, rownum AS rnum FROM (SELECT * FROM vwCrtDetails
AND ${column} LIKE '%' || #{word} || '%'
ORDER BY crtSeq DESC) b) WHERE rnum BETWEEN #{begin} AND #{end}

SELECT * FROM (SELECT b.*, rownum AS rnum FROM (SELECT * FROM tblBook
AND ${column} LIKE '%' || #{word} || '%'
ORDER BY bookSeq DESC) b) WHERE rnum BETWEEN #{begin} AND #{end}

SELECT * FROM (SELECT b.*, rownum AS rnum FROM (SELECT * FROM tblAcademy
AND ${column} LIKE '%' || #{word} || '%'
ORDER BY academySeq DESC) b) WHERE rnum BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE tblBoard
SET isboardshow = 0
WHERE boardSeq = #{boardSeq}

UPDATE tblBoard
SET isboardshow = 1
WHERE boardSeq = #{boardSeq}

mapper.select:
SELECT * FROM (SELECT b.*, rownum AS rnum FROM (SELECT * FROM tblUserInfo
AND ${column} LIKE '%' || #{word} || '%'
ORDER BY joinDate DESC) b) WHERE rnum BETWEEN #{begin} AND #{end}

SELECT * FROM (SELECT b.*, rownum AS rnum FROM (SELECT * FROM JobView
AND ${column} LIKE '%' || #{word} || '%'
ORDER BY jobSeq DESC) b) WHERE rnum BETWEEN #{begin} AND #{end}

SELECT * FROM (SELECT b.*, rownum AS rnum FROM (SELECT * FROM tblDepart
AND ${column} LIKE '%' || #{word} || '%'
ORDER BY departSeq ASC) b) WHERE rnum BETWEEN #{begin} AND #{end}
