mapper.select:
SELECT b.*, (SELECT count(*) FROM tblcrtlike where crtseq = b.crtseq
and id LIKE '%' || #{id} || '%') as crtlike
)
FROM (SELECT a.*, rownum AS rnum FROM vwCrt a WHERE crtName IS NOT NULL
AND crtName LIKE '%' || #{word} || '%'
AND crtCtg LIKE '%' || #{crtCtg} || '%'
AND agency LIKE '%' || #{agency} || '%'
AND difficulty LIKE '%' || #{difficulty} || '%'
) b WHERE rnum BETWEEN #{begin} AND #{end}
